var BaseController = require('../base');
var barnacleMode = require('barnacle-mode');

function StreamActionController(options) {
  options = options || {};
  BaseController.apply(this, arguments);
  this._config = options.config;
  this._navigation = options.navigation;
}

StreamActionController.prototype = Object.create(BaseController.prototype, {
  constructor: { value: StreamActionController }
});

StreamActionController.prototype._createActionStream = function (name) {
  return barnacleMode(this[name].bind(this));
};

module.exports = StreamActionController;
