function GetPublishedPosts(options) {
  this._postFinder = options.postFinder;
}

GetPublishedPosts.prototype.exec = function (done) {
  this._postFinder.published(null, done);
}

module.exports = function getPublishedPosts(options, done) {
  var gpp = new GetPublishedPosts(options || {});

  gpp.exec(done);
};
